<script>import { getContext, onMount, tick } from "svelte";
import { doNothingAction } from "./actions";
import { TIPTAP_NODE_VIEW } from "./context";
const { onDragStart } = getContext(TIPTAP_NODE_VIEW);
let element;
export let action = doNothingAction;
export let as = "div";
onMount(async () => {
  await tick();
  element.style.whiteSpace = "normal";
});
</script>

<svelte:element
  this={as}
  bind:this={element}
  data-node-view-wrapper=""
  on:dragstart={onDragStart}
  use:action
  {...$$restProps}
>
  <slot />
</svelte:element>
